配置 herd
// Pampasfile.js

module.exports = {
  root: process.cwd(),
  port: 8081,
  designer: {
    enable: false,
  },
  static: {
    root: &#39;public&#39;,
    prefix: &#39;/&#39;,
  },
  templete: &#39;handlebars&#39;,
  handlebars: {
    components: &#39;public/components&#39;,
    views: &#39;public/views&#39;,
  },
  session: {
    store: &#39;cookie&#39;,
    cookieDomain: &#39;localhost:8081&#39;,
    prefix: &#39;sessionId&#39;,
    maxAge: 1000 * 60 * 30,
  },
