// mock-file.js

const data = []

for (let i = 0; i &lt; 10; i++) {
  data.push({
    id: i,
    key: `key${i}`,
    value: `value${i}`,
    description: `description${i}`,
    scope: [&#39;car&#39;, &#39;house&#39;, &#39;phone&#39;][i % 3],
    createdAt: `2009-1-${i}`,
    updatedAt: `2013-${i}-9`,
  })
}

module.exports = {
  services: {
    getList(context) {
      const { scope } = context
      if (!scope) return data
      const result = []
      data.forEach((d) =&gt; {
        if (d.scope === scope) {
          result.push(d)
        }
      })
      return result
    },
  },
  routes: {},
}


